(()=>{"use strict";const a=async()=>`\n  <div class="Characters">\n  ${(await(async()=>{for(let a=1;a<=5;a++){const n=`https://pokeapi.co/api/v2/pokemon/${a}`;try{const a=await fetch(n),e=await a.json();return console.log(e),e}catch(a){console.log("Error ðŸ˜®",a)}}})()).results.map((a=>`\n    <article class="Characters-item">\n    <a href="#/${a.id} ">\n    <img src="${a.image}" alt="${a.name}"/>\n    <h2>${a.name} </h2>\n    </a>\n  </article>`)).join("")}\n  </div>\n  `,n=async()=>{const n=document.getElementById("header"),e=document.getElementById("content");n.innerHTML=await'\n<div class="Header-main">\n  <div className="Header-logo">\n    <h1>\n      <a href="">\n        PokeApi\n      </a>\n    </h1>\n  </div>\n  <div className="Header-nav">\n    <a href="#/about/">About</a>\n  </div>\n</div>\n';let t=location.hash.slice(1).toLocaleLowerCase().split("/")[1]||"/";var i;await(i=t,i.length<=3?"/"===i?i:"/:id":i),e.innerHTML=await a()};window.addEventListener("load",n),window.addEventListener("hashchange",n)})();